import React from "react";
import { Row, Col } from "reactstrap";

const Marketplace = () => {
    const couponItems = [
        { title: "Coupon 1", description: "Description 1", price: "$5.00" },
        { title: "Coupon 2", description: "Description 2", price: "$10.00" },
        { title: "Coupon 3", description: "Description 3", price: "$7.50" },
        { title: "Coupon 4", description: "Description 4", price: "$12.00" },
        { title: "Coupon 5", description: "Description 5", price: "$8.50" },
        { title: "Coupon 6", description: "Description 6", price: "$15.00" },
    ];

    const itemsPerRow = 3;

    const rows = [];
    for (let i = 0; i < couponItems.length; i += itemsPerRow) {
        const rowItems = couponItems.slice(i, i + itemsPerRow);
        rows.push(
            <Row key={i}>
                {rowItems.map((coupon, index) => (
                    <Col key={index} sm="4" className="coupon-item">
                        <h2>{coupon.title}</h2>
                        <p>{coupon.description}</p>
                        <p>{coupon.price}</p>
                        <button className="buy-button">Buy</button>
                    </Col>
                ))}
            </Row>
        );
    }

    return (
        <div className="marketplace-container">
            <h1>Marketplace</h1>
            <div className="coupon-grid">
                {rows}
            </div>
        </div>
    );
};

export default Marketplace;
