import React, { useState } from "react";
import { Container, Table, Button } from "reactstrap";

const Dashboard = () => {
    const [reportData, setReportData] = useState([
        { id: 1, date: "2023-08-10", reporter: "User A", status: "Pending" },
        { id: 2, date: "2023-08-11", reporter: "User B", status: "Pending" },
        { id: 3, date: "2023-08-12", reporter: "User C", status: "Pending" },
        { id: 4, date: "2023-08-13", reporter: "User D", status: "Pending" },
        { id: 5, date: "2023-08-14", reporter: "User E", status: "Pending" },
        { id: 1, date: "2023-08-10", reporter: "User A", status: "Pending" },
        { id: 2, date: "2023-08-11", reporter: "User B", status: "Pending" },
        { id: 3, date: "2023-08-12", reporter: "User C", status: "Pending" },
        { id: 4, date: "2023-08-13", reporter: "User D", status: "Pending" },
        { id: 5, date: "2023-08-14", reporter: "User E", status: "Pending" },
        { id: 1, date: "2023-08-10", reporter: "User A", status: "Pending" },
        { id: 2, date: "2023-08-11", reporter: "User B", status: "Pending" },
        { id: 3, date: "2023-08-12", reporter: "User C", status: "Pending" },
        { id: 4, date: "2023-08-13", reporter: "User D", status: "Pending" },
        { id: 5, date: "2023-08-14", reporter: "User E", status: "Pending" },
    ]);

    const handleAuthorize = (id) => {
        // Update the status to "Authorized" for the given report ID
        const updatedReportData = reportData.map((report) =>
            report.id === id ? { ...report, status: "Authorized" } : report
        );
        setReportData(updatedReportData);
    };

    const handleDeny = (id) => {
        // Update the status to "Denied" for the given report ID
        const updatedReportData = reportData.map((report) =>
            report.id === id ? { ...report, status: "Denied" } : report
        );
        setReportData(updatedReportData);
    };

    return (
        <Container>
            <h1 className="text-center mt-5">Dashboard</h1>
            <div className="table-container">
                <Table className="mt-4">
                    <thead>
                    <tr>
                        <th>Report ID</th>
                        <th>Date</th>
                        <th>Reporter</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {reportData.map((report) => (
                        <tr key={report.id}>
                            <td>{report.id}</td>
                            <td>{report.date}</td>
                            <td>{report.reporter}</td>
                            <td>
                                {report.status === "Pending" && (
                                    <>
                                        <Button
                                            color="success"
                                            onClick={() => handleAuthorize(report.id)}
                                            className="mr-2"
                                        >
                                            Authorize
                                        </Button>
                                        <Button
                                            color="danger"
                                            onClick={() => handleDeny(report.id)}
                                        >
                                            Deny
                                        </Button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
            </div>
        </Container>
    );
};

export default Dashboard;
