import React, { useState } from "react";
import { Container, Table, Button } from "reactstrap";

const Dashboard = () => {
    const [reportData, setReportData] = useState([
        { id: 1, date: "2023-08-10", reportName: "Report 1", reportDescription: "Description 1", status: "Pending", reporter: "User A" },
        { id: 2, date: "2023-08-11", reportName: "Report 2", reportDescription: "Description 2", status: "Pending", reporter: "User B" },
        { id: 3, date: "2023-08-12", reportName: "Report 3", reportDescription: "Description 3", status: "Pending", reporter: "User C" },
        // ... (add more report data)
    ]);

    const handleAuthorize = (id) => {
        // Update the status to "Authorized" for the given report ID
        const updatedReportData = reportData.map((report) =>
            report.id === id ? { ...report, status: "Authorized" } : report
        );
        setReportData(updatedReportData);
    };

    const handleDeny = (id) => {
        // Update the status to "Denied" for the given report ID
        const updatedReportData = reportData.map((report) =>
            report.id === id ? { ...report, status: "Denied" } : report
        );
        setReportData(updatedReportData);
    };

    return (
        <Container>
            <h1 className="text-center mt-5">Dashboard</h1>
            <div className="table-container">
                <Table className="mt-4">
                    <thead>
                    <tr>
                        <th>Report ID</th>
                        <th>Date</th>
                        <th>Report Name</th>
                        <th>Report Description</th>
                        <th>Status</th>
                        <th>Reporter</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {reportData.map((report) => (
                        <tr key={report.id}>
                            <td>{report.id}</td>
                            <td>{report.date}</td>
                            <td>{report.reportName}</td>
                            <td>{report.reportDescription}</td>
                            <td>{report.status}</td>
                            <td>{report.reporter}</td>
                            <td>
                                {report.status === "Pending" && (
                                    <>
                                        <Button
                                            color="success"
                                            onClick={() => handleAuthorize(report.id)}
                                            className="mr-2"
                                        >
                                            Authorize
                                        </Button>
                                        <Button
                                            color="danger"
                                            onClick={() => handleDeny(report.id)}
                                        >
                                            Deny
                                        </Button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
            </div>
        </Container>
    );
};

export default Dashboard;
