import React, { useState } from "react";
import { Container, Table, Button, Modal, ModalHeader, ModalBody } from "reactstrap";
import { withAuthenticationRequired } from "@auth0/auth0-react";
import "./admin.css"; // Import the admin.css file

const Dashboard = () => {
    const [reportData, setReportData] = useState([
        { id: 1, date: "2023-08-10", reportName: "Report 1", reportDescription: "Description 1dasdsadadsdasdsadssddddsdjkhsjkdhfskhfjkehkhajfhesghehfwefjefheougsofheoufoehfouehoguhweoifheouhfohohooeh", status: "Pending", reporter: "User A", imageUrl: "https://cdn.discordapp.com/attachments/1110981241618038857/1138147768788070410/Screenshot_2566-08-07_at_23.32.09.png" },
        { id: 2, date: "2023-08-11", reportName: "Report 2", reportDescription: "Description 2", status: "Pending", reporter: "User B", imageUrl: "" },
        // ... (more report data)
    ]);

    const [popupImage, setPopupImage] = useState({ visible: false, url: "" });

    const handleAuthorize = (id) => {
        const updatedReportData = reportData.map((report) =>
            report.id === id ? { ...report, status: "Authorized" } : report
        );
        setReportData(updatedReportData);
    };

    const handleDeny = (id) => {
        const updatedReportData = reportData.map((report) =>
            report.id === id ? { ...report, status: "Denied" } : report
        );
        setReportData(updatedReportData);
    };

    const handleOpenDescription = (description) => {
        setPopupDescription(description);
    };

    const handleCloseDescription = () => {
        setPopupDescription("");
    };

    return (
        <Container>
            <Table className="mt-4">
                <thead>
                {/* ... (your table headers) */}
                </thead>
                <tbody>
                {reportData.map((report) => (
                    <tr
                        key={report.id}
                        onClick={() => handleOpenDescription(report.reportDescription)}
                        style={{ cursor: "pointer" }}
                        className="hover-grey"
                    >
                        <td>{report.id}</td>
                        <td>{report.date}</td>
                        <td>{report.reportName}</td>
                        <td>{report.reportDescription}</td>
                        <td>{report.status}</td>
                        <td>{report.reporter}</td>
                        <td>
                            {report.status === "Pending" && (
                                <>
                                    <Button
                                        color="success"
                                        onClick={() => handleAuthorize(report.id)}
                                        className="mr-2"
                                    >
                                        Authorize
                                    </Button>
                                    <Button
                                        color="danger"
                                        onClick={() => handleDeny(report.id)}
                                    >
                                        Deny
                                    </Button>
                                </>
                            )}
                        </td>
                    </tr>
                ))}
                </tbody>
            </Table>
            <Modal isOpen={popupDescription !== ""} toggle={handleCloseDescription}>
                <ModalHeader toggle={handleCloseDescription}>Report Description</ModalHeader>
                <ModalBody>
                    {popupDescription}
                    <Button color="secondary" onClick={handleCloseDescription} className="mt-3">
                        Close
                    </Button>
                </ModalBody>
            </Modal>
        </Container>
    );
};

export default withAuthenticationRequired(Dashboard, {});
